import { useNavigate } from "react-router-dom";
import { useState } from "react";
import EditPatient from "./editPatient";

import { Modal, ModalBody, ModalHeader } from "reactstrap";

const ViewPatient = () => {
  const [modal, setModal] = useState(false);

  const navigate = useNavigate();

  const patientList = [
    {
      Id: "101",
      Name: "test",
      Address: "pune",
      Mobile_No: "9075436019",
    },
    {
      Id: "102",
      Name: "Reshma",
      Address: "pune",
      Mobile_No: "9075436010",
    },

    {
      Id: "103",
      Name: "Pooja",
      Address: "pune",
      Mobile_No: "9075436010",
    },
  ];

  const addNewPatient = () => {
    navigate("/addNewPatient");
  };

  return (
    <div className="container" >
      <div>
        <h1 style={styles.h3}>All patient</h1>
      </div>
      

      <button
        onClick={() => addNewPatient()}
        
        className="btn_style"
      >
        Add new patient
      </button>

      <button
        
        className="btn_style"
        onClick={() => {
          navigate("/signin");
        }}
      >
        Logout
      </button>

      <div style={{ margin: 20 }}>
        <div className="col-sm-3">
          <label>First Name</label>
          <input type="text" className="form-control"></input>
        </div>

        <div className="col-sm-3">
          <label>Mobile no</label>
          <input type="number" className="form-control"></input>
        </div>
        <div>
          <button
             className="btn_style"
            type="submit"
            Style="margin-top:20px"
          >
            Search
          </button>
        </div>
      </div>

      <table
        cellpadding="0"
        cellspacing="0"
        border="0"
        class="datatable table table-striped table-bordered"
      >
        <thead>
          <tr>
            <th>Patient Id</th>
            <th>Name</th>
            <th>Address</th>
            <th>Mobile No</th>
            <th>Details</th>
          </tr>
        </thead>
        <tbody>
          {patientList.map((patient) => {
            return (
              <tr>               
                <td>{patient.Id}</td>
                <td>{patient.Name}</td>
                <td>{patient.Address}</td>
                <td>{patient.Mobile_No}</td>
                <td>
                  <button
                    
                    className="btn_style"
                    onClick={() => setModal(true)}
                    data-toggle="modal"
                    data-target="#exampleModal"
                  >
                    Edit
                  </button>

                  <button
                    
                    className="btn_style"
                    onClick={()=>{
                      navigate('/createAppointment')
                    }}
                  >
                    Appointement
                  </button>
               

                  <button
                    
                    className="btn_style"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
      <Modal
        size="md"
        isOpen={modal}
        toggle={() => {
          setModal(!modal);
        }}
      >
        <ModalHeader
          toggle={() => {
            setModal(!modal);
          }}
        ></ModalHeader>
        <ModalBody>         
          <EditPatient/>
        </ModalBody>
      </Modal>
    </div>
  );
};

const styles = {
  h3: {
    textAlign: "center",
    margin: 40,
  },
  
};

export default ViewPatient;
