 import { useState } from "react";
// import { ToastContainer, toast } from 'react-toastify';
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Signin = () => {
  const [name, setname]= useState('');
  const [password, setPassword]= useState('');
  
  const navigate = useNavigate();
  
    const signin = () => {
    console.log(name);
    console.log(password);
   
    var data = JSON.stringify({
      "name": name,
      "password": password
    });
    
    var config = {
      method: 'post',
      url: 'http://localhost:8080/hms/login/signin',
      headers: { 
        'Content-Type': 'application/json', 
        
      },
      data : data
    };
    
    axios(config)
    .then(function (response) {
      
      console.log(response.data);
      
      sessionStorage.setItem('token',response.data.jwt);
      sessionStorage.setItem('empId',response.data.empId);
      sessionStorage.setItem('deptId',response.data.deptId);
      navigate("/appointmentList");
     
      
    })
    .catch(function (error) {
      console.log(error);
    });
    
    
    
  };

  return (
    <div className="container" Style="display: flex;
    justify-content: center;align-items: center;margin-top:4%">
      <div style={styles.container} >
        <h3 style={styles.h3}> Sign in here</h3>
    
      <form >
        <div Style="border:none">
          <div>
            <div className="mb-3">
              <label>
                <b>name:</b>
              </label>
              <input
                style={styles.input}
                className="form-control"
                type="text"
                onChange={(event) => {
                  setname(event.target.value)
                }}
              />
            </div>

            <div className="mb-3">
              <label>
                <b>Password:</b>
              </label>
              <input
              onChange={(event) => {
                setPassword(event.target.value)
              }}
                style={styles.input}
                className="form-control"
                type="password"
              />
            </div>

            

            

            
            
            

            <div className="mb-3" style={{ marginTop: 40 }}>
              <button                 
                onClick={() => {
                  
                  signin()
                  
                }}
                className="btn_style"
              >
                Signin
              </button>
            </div>
          </div>
        </div>
      </form>
      </div>
    </div>
  );
};

const styles = {
  container: {
    width: 400,
    height: 550,
    padding: 20,
    position: "relative",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    
     
    borderColor: "rgba(6, 99, 88)",
    borderRadius: 10,
    broderWidth: 3,
    borderStyle: "solid",
    boxShadow: "1px 1px 20px 5px #C9C9C9",
  },
  input: {
    width: "100%",
  },

    h3: {
      textAlign: "center",
      margin: 20,
    }

};
export default Signin;
