import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useState } from "react";
import React, { useEffect } from "react";
import { ToggleSlider }  from "react-toggle-slider";
const AppointmentList = () => {
  const [result, setresult] = useState([])
  const [active, setActive] = useState(false);
  
  useEffect(() => {
    //window.scrollTo(0, 0);
    var configadmit = {
      method: 'get',
      url: 'http://localhost:8083/hms/doctor/getMyAdmit',
      headers: { 
        'id': '2', 
        'Authorization': 'Bearer eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJhZG1pbm5hbWUxIiwiaWF0IjoxNjYzMTUzMjUxLCJleHAiOjE2NjMyMzk2NTF9.T2ibUWvpcW3GjAqIIxvp9u_MzqR4AOEtXSA51SNNYE4FWVPS-WIRMiKEacGWXHWEhcjMySaQYn5V863GmYbTPg', 
        'Cookie': 'jwtoken=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MjliMzJkMmExMzgzYWE5MTk4YThlNWMiLCJpYXQiOjE2NTQzMzg0MjV9.O1FKJXaSxNeNVHnfO8pUz4G3lx1g_iyEPenub17I-VE'
      }
    };
    
    axios(configadmit)
    .then(function (response) {
      console.log(JSON.stringify(response.data));
    })
    .catch(function (error) {
      console.log(error);
    });
    
    var config = {
      method: 'get',
      url: 'http://localhost:8083/hms/doctor/getMyAppointment',
      headers: { 
        'id': sessionStorage.getItem("empId"), 
        'Authorization': 'Bearer '+sessionStorage.getItem('token')
      }
    };
    
    axios(config)
    .then(function (response) {
     // console.log(JSON.stringify(response.data));
     
      
      setresult(response.data);
      console.log(response.data)
    })
    .catch(function (error) {
      console.log(error);
    });
  }, []);
  const navigate = useNavigate();

  

  return (
    <div className="container ">
      <ToggleSlider onToggle={state => setActive(!active)}/>
      <div>
        <h2 style={styles.h2}>Appointment List</h2>
      </div>
      <div>
        <table class="table" Style="text-align:center; ">
          <thead>
            <tr>
              <th scope="col">id</th>
              <th scope="col">Name</th>
              <th scope="col">date</th>
              <th scope="col">slot</th>
              <th scope="col">action</th>
            </tr>
          </thead>
          <tbody>
          {result.map((appointment) => {
            return (
              <tr>               
                <td>{appointment.appointmentId}</td>
                <td>{appointment.patientId.name}</td>
                <td>{appointment.dateTime.substring(0,10)}</td>
                <td>{appointment.dateTime.substring(11)}</td>
                
                <td>
                <button
                  onClick={() => {
                    navigate("/appointments",{state:{data:appointment}});
                  }}
                  type="submit"
                  className="btn_style"
                >
                  Attend
                </button>
                <button type="submit" className="btn_style">
                  Discard
                </button>
              </td>
              </tr>
            );
          })}
        </tbody>
          </table>
      </div>
    </div>
  );
};

const styles = {
  h2: {
    textAlign: "center",
    margin: 20,
  }
};
export default AppointmentList;
