import TagsInput from '../../components/tagsinput'
import { useNavigate } from 'react-router-dom';
import {useLocation} from "react-router-dom";
import {CDropdown,CDropdownToggle,CDropdownMenu,CDropdownItem} from '@coreui/react'
import axios from "axios";
import React,{useEffect} from 'react'
import { useState } from "react";
const AdmitForm = () => {
  const location = useLocation();
  const [AllMeds, setAllMeds] = useState([])
  const [alltests, setalltests] = useState([])
  const [meds, setmeds] = useState([])
  const [tests, settests] = useState([])
  const [prescription, setprescription] = useState("")
  const medsId=[]
  const testsId=[]
  const admitDetails=location.state.data
  const setIds=()=>{
    meds.map((t)=>{medsId.push(t.medicineId);console.log(medsId);})
    tests.map((t)=>{testsId.push(t.testId);console.log(testsId);})
    //tests.map((t)=>{settestsId(...testsId, t.testId );console.log(t.testId)})
    // console.log("meds"+JSON.stringify(meds))
    // console.log("medsif"+medsId)
    // console.log("test"+JSON.stringify(tests))
    // console.log("testid"+testsId)


   }
   const update=()=>{
    var data = JSON.stringify({
      "prescription": prescription,
      "admitId": admitDetails.admitId,
      "tests": testsId,
      "medicines": medsId
    });
    
    var config = {
      method: 'put',
      url: 'http://localhost:8083/hms/doctor/getMyAppointment/admit/update',
      headers: { 
        'Authorization': 'Bearer '+sessionStorage.getItem('token'), 
        'Content-Type': 'application/json', 
        },
      data : data
    };
    
    axios(config)
    .then(function (response) {
      console.log(JSON.stringify(response.data));
    })
    .catch(function (error) {
      console.log(error);
    });
    
   }
   const discharge=()=>{
    var data = JSON.stringify({
      "appointId": admitDetails.appointmentId.appointmentId,
      "admitId": admitDetails.admitId
    });
    
    var config = {
      method: 'post',
      url: 'http://localhost:8083/hms/doctor/getMyAppointment/admit/discharge',
      headers: { 
        'Authorization': 'Bearer '+sessionStorage.getItem(token), 
        'Content-Type': 'application/json', 
        },
      data : data
    };
    
    axios(config)
    .then(function (response) {
      console.log(JSON.stringify(response.data));
    })
    .catch(function (error) {
      console.log(error);
    });
    
   }
  //console.log(location.state.data)
    React.useEffect(() => {
        window.scrollTo(0, 0);
        var configmeds = {
          method: 'get',
          url: 'http://localhost:8083/hms/doctor/getAllMeds',
          headers: { 
            'Authorization': 'Bearer '+sessionStorage.getItem('token'), 
            }
        };
        
        axios(configmeds)
        .then(function (response) {
          console.log(JSON.stringify(response.data));
          setAllMeds(response.data)
        })
        .catch(function (error) {
          console.log(error);
        });
        
        var configtest = {
          method: 'get',
          url: 'http://localhost:8083/hms/doctor/getAllTests',
          headers: { 
            'Authorization': 'Bearer '+sessionStorage.getItem('token')
             }
        };
        
        axios(configtest)
        .then(function (response) {
          console.log(JSON.stringify(response.data));
          setalltests(response.data)
        })
        .catch(function (error) {
          console.log(error);
        });
      }, []);
    const navigate= useNavigate();
    const selectedTags = tags => console.log(tags);
          return (
              
              <div className="container" >
                   <div Style=" w-100 
                margin-top: 10px;
                margin-bottom: 10px;
                padding:10px;
                box-shadow: 0px 0px 4px #093329;background:#004763 ;
                ">
                    <h1 Style="margin:10px;color:white;">Admit form</h1>
                </div>
                <div Style=" padding:20px;box-shadow: 0px 0px 4px #093329;margin-top:20px;margin-bottom:20px">
                  <table className="table table-bordered ">
                    <tr><td Style="width:30%; text-align:left"><label>Name</label></td>
