import { useState ,useEffect} from "react";
import axios from "axios";
import { toast } from 'react-toastify'
import { useLocation, useNavigate } from 'react-router-dom'

const EditPatient = () => {
    const [name, setName] = useState('');
    const [birthDate, setBirthDate] = useState('');
    const [address, setAddress] = useState('');
    const [mobileNo, setMobileNo] = useState('');    

    const navigate = useNavigate() ;

    const location = useLocation();
    const patientTemp=location.state.data;

   const editPatientDetails = () => {
       
  
    if (name.length === 0) {
        toast.error('enter name')
      } else if (birthDate.length === 0) {
        toast.error('enter birthDate')
      } else if (address.length === 0) {
        toast.error('enter address')
      } else if (mobileNo.length === 0) {
        toast.error('enter mobileNo')
      }  else {
        const body = {
            patientId:patientTemp.patientId,
            name,
            birthDate,
            address,
            mobileNo 
        }          

        var config = {
            method: 'put',
            url: 'http://localhost:8082/hms/receptionist/updatePatientDetails',
            headers: { 
                'Authorization': 'Bearer '+sessionStorage.getItem('token'),
                'Content-Type': 'application/json'
            },
            data : body
          };          
          axios(config)
            .then(function (response) {
            navigate('/viewPatient')
          
           // console.log(JSON.stringify(response.data));
            })
            .catch(function (error) {
             console.log(error);
            });   
    }
}
    return (          
            <div className="container div_style" >            
              <div >
                <div>
                <div className='mb-3'>
                        <label>Patient Id </label>
                        <input                    
                            type="number"
                            className='form-control' value={patientTemp.patientId}
                            >
                        </input>
                    </div>  
                   <div className='mb-3'>
                        <label>Name</label>
                        <input                    
                            type="text"
                            className='form-control' 
                            onChange={(e) => {
                            setName(e.target.value)
                                }} placeholder={patientTemp.name}>
                        </input>
                    </div>
                    <div className='mb-3'>
                        <label>Mobile no</label>
                        <input                    
                            type="number"
                            className='form-control'
                            onChange={(e) => {
                            setMobileNo(e.target.value)
                            }} placeholder={patientTemp.mobileNo}>
                        </input>
                    </div> 
        
                    <div className='mb-3'>
                        <label>Address</label>
                        <input                    
                            type="text"
                            className='form-control'
                            onChange={(e) => {
                            setAddress(e.target.value)
                            }} placeholder={patientTemp.address}>
                        </input>
                    </div>                      
        
                    <div className='mb-3'>
                        <label>Date Of Birth</label>
                        <input                    
                            type="date"
                            className='form-control'
                            onChange={(e) => {
                            setBirthDate(e.target.value)
                            }} placeholder={patientTemp.birthDate}>
                        </input>
                    </div>
        
                    {/* <div className='mb-3'>
                        <label Style="margin-right:30px">Gender</label>
                        <div class="form-check form-check-inline">
                            <input class="form-check-input" type="radio" name="inlineRadioOptions" id="inlineRadio1" value="option1"></input>
                            <label class="form-check-label" for="inlineRadio1" >Male</label>
                        </div>
                        <div class="form-check form-check-inline">
                            <input class="form-check-input" type="radio" name="inlineRadioOptions" id="inlineRadio2" value="option2"></input>
                            <label class="form-check-label" for="inlineRadio2">Female</label>
                        </div>
                    </div> */}
                    <div class="text-center">
                         <button type="button"  className="btn_style" onClick={editPatientDetails}>Update Changes</button>
                    </div>
                </div> 
              </div>
            </div>
          );
  };
  
  
  const styles = {
    h3: {
      textAlign: "center",
      margin: 20,
    }
  };  
  
  export default EditPatient;
