import { useNavigate } from 'react-router-dom'
import { useState ,useEffect} from "react";
import axios from "axios";
import { Modal, ModalBody, ModalHeader } from "reactstrap";

const InvoiceList = () => {


  const [modal, setModal] = useState(false);
  const [invoice, setInvoice] = useState([]);
  const [searchData, setSearchData] = useState("")

  // load the patients as soon as the component gets loaded successfully
  useEffect(() => {   
    var config = {
      method: 'get',
      url: 'http://localhost:8084/hms/accountant/bill',
      headers: { 
        'Authorization': 'Bearer '+sessionStorage.getItem('token')
      }
    };
    
    axios(config)
    .then(function (response) {
      console.log(JSON.stringify(response.data));
      setInvoice(response.data)
      console.log(response.data[0].appointmentId.patient.name)
    })
    .catch(function (error) {
      console.log(error);
    });
    
  }, [])

  const navigate = useNavigate();
    
  const invoiceList = () => {
      navigate('/invoice')
  }

    return (
        <div className="container">
       
  
        
        <button  className="btn_style" style={{ marginTop:30 ,marginLeft:900}}  onClick={()=>{
            navigate('/')
        }}>
          Logout
        </button>

        <h3 style={styles.h3}>Display patient invoice</h3>

        <form>
        <input
          className="mr-md-2"
          style={{border:"solid", borderColor:" rgba(5, 163, 144)", backgroundColor:" rgba(5, 163, 144,0.3)",Color:"white" ,margin:30}}
          type="search"
          placeholder="Search patient"
          aria-label="Search"
          onChange={(e) => {
            setSearchData(e.target.value)
          }}
        />

      </form>
  
        <table class="datatable table table-striped table-bordered">
          <thead>
            <tr>
              <th>Patient Id</th>
              <th>Name</th>
              <th>Payment Status</th>
              <th>Details</th>
            </tr>
          </thead>
          <tbody>

          {invoice.filter(p=>p.name.toLowerCase().includes(name.toLowerCase())).map((Invoice) => {
                  return ( 
            <tr>
               <td>{invoice.billId}</td> 
              <td>{invoice.appointmentId.patient.name}</td>
              <td>{invoice.payementStatus}</td>
        
              <td>              
                <button onClick={() => invoiceList()} className="btn_style">
                 Invoice
                </button>
              </td>
            </tr>
             )
                })} 
          </tbody>
        </table>
      </div>
    );
  };
  
  const styles = {
    h3: {
      textAlign: "center",
      margin: 20,
    },
    button: {
      marginRight: 10,
    },
  };
    


export default InvoiceList
